import React from "react";
import "./Login.css";
class Login extends React.Component {
  state = {};
  render() {
    return (
      <div>
        <LoginCheck />
      </div>
    );
  }
}

class LoginCheck extends React.Component {
  state = {
    loggedIn: false
  };
  handleLogin() {
    this.setState({ loggedIn: true });
  }
  handleLogout() {
    this.setState({ loggedIn: false });
  }
  render() {
    return (
      <div>
        {this.state.loggedIn && (
          <div>
            <h3>Welcome, user!</h3>
            <GroceryList />
            <button onClick={() => this.handleLogout()}>Logout</button>
          </div>
        )}
        {!this.state.loggedIn && (
          <div>
            <h3>Please click to login</h3>
            <h4>Email address</h4>
            <input type="text"></input>
            <h4>Password</h4>
            <input type="text"></input>
            <br />
            <button onClick={() => this.handleLogin()}>Login</button>
            <button onClick={() => this.handleLogin()}>Signup</button>
          </div>
        )}
      </div>
    );
  }
}

class GroceryList extends React.Component {
  state = {
    groceryList: [],
    groceryItem: ""
  };
  handleValueEntered(event) {
    const newGroceryItem = event.target.value;
    console.log(">>>>>>>>>>" + newGroceryItem);
    this.setState(state => ({ ...state, groceryItem: newGroceryItem }));
  }
  handleGroceryAdded() {
    this.setState(state => {
      const newGroceryList = [...state.groceryList, state.groceryItem];
      return { ...state, groceryList: newGroceryList };
    });
    console.log("click");
  }

  render() {
    const groceryElements = this.state.groceryList.map((groceryItem, index) => (
      <li key={index}>{groceryItem}</li>
    ));
    return (
      <div>
        <h2>GA Airlines</h2>
        <input type="text" onChange={event => this.handleValueEntered(event)} />
        <button onClick={() => this.handleGroceryAdded()}>Add to list</button>
        <ul>{groceryElements}</ul>
      </div>
    );
  }
}

export default Login;
